mapper.select:
select TO_CHAR(start_date, 'YYYY-MM-DD') as start_date, TO_CHAR(end_date, 'YYYY-MM-DD') as end_date
from reservation 
where product_no = #{pno}

select r.no,p.name as product_name, r.start_date, r.end_date, r.amount,r.status, r.is_accepted, r.accepted_date, buy.name as buyer_name, buy.id as buyer_id, buy.mno as buyer_no, sell.name as seller_name, sell.id as seller_id, sell.mno as seller_no from reservation r inner join products p on r.product_no=p.no inner join (select mno, id, name from member ) buy on r.buyer_no = buy.mno inner join (select mno, id, name from member ) sell on p.seller_no = sell.mno
where p.name like #{keyword}
where buy.id like #{keyword}
where sell.id like #{keyword}
order by r.no desc
